import React, { useState } from "react";
import { resumeUpdater } from "../../utils";

const Projects = (...props) => {
  const { resume, setResume } = props[0];
  const [projectFormCount, setprojectFormCount] = useState(() => {
    const getprojectFormCount = parseInt(
      localStorage.getItem("projectFormCount")
    );
    if (!getprojectFormCount) return 1;
    return getprojectFormCount;
  });
  const changeResumeInfo = (page, key, value, internal_key) => {
    resumeUpdater(resume, setResume, page, key, value, internal_key);
  };
  const addAnotherForm = () => {
    localStorage.setItem("projectFormCount", projectFormCount + 1);
    setprojectFormCount((preVal) => preVal + 1);
  };

  return (
    <div className="flex flex-col">
      {Array.from(Array(projectFormCount).keys()).map((el) => {
        return (
          <form>
            <div class="grid gap-6 mb-6 md:grid-cols-2" {...props}>
              <div>
                <label for="first_name" class="block mb-2 text-sm font-medium ">
                  Project Title
                </label>
                <input
                  type="text"
                  id="first_name"
                  class="bg-gray-50 border border-gray-300  text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5  dark:border-gray-600 dark:placeholder-gray-400  dark:focus:ring-blue-500 dark:focus:border-blue-500"
                  placeholder="Project Title"
                  value={resume.project[el] && resume.project[el].project_title}
                  onChange={(e) =>
                    changeResumeInfo(
                      "project",
                      "project_title",
                      e.target.value,
                      el
                    )
                  }
                  required
                ></input>
              </div>
            </div>

            <label
              for="message"
              class="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
            >
              Description
            </label>
            <textarea
              id="message"
              rows="4"
              class="block p-2.5 w-full text-sm   rounded-lg border border-gray-300 focus:ring-blue-500 focus:border-blue-500  dark:border-gray-600 dark:placeholder-gray-400  dark:focus:ring-blue-500 dark:focus:border-blue-500"
              placeholder=" Talk a little bit about your projects."
              value={
                resume.project[el] && resume.project[el].project_description
              }
              onChange={(e) =>
                changeResumeInfo(
                  "project",
                  "project_description",
                  e.target.value,
                  el
                )
              }
            ></textarea>
            <div>
              <label
                for="website"
                class="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
              >
                Project URL
              </label>
              <input
                type="url"
                id="website"
                class=" border border-gray-300   text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5  dark:border-gray-600 dark:placeholder-gray-400  dark:focus:ring-blue-500 dark:focus:border-blue-500"
                placeholder="project.com"
                value={resume.project[el] && resume.project[el].project_link}
                onChange={(e) =>
                  changeResumeInfo(
                    "project",
                    "project_link",
                    e.target.value,
                    el
                  )
                }
                required
              ></input>
            </div>
          </form>
        );
      })}
      <div
        onClick={addAnotherForm}
        className="rounded-[50%] self-center mt-4 text-[14px] cursor-pointer flex justify-center items-center text-white  bg-[#353535]   w-[34px] h-[34px] text-center  "
      >
        +
      </div>
    </div>
  );
};

export default Projects;
