import { useState } from "react";
import About from "./components/Form/About";
import Experience from "./components/Form/Experience";
import SideNavbar from "./components/SideNavbar";
import UserResume from "./components/UserResume";

const FormComponent = ({ component, ...props }) => {
  switch (component) {
    case "about":
      return <About {...props} />;
    case "experience":
      return <Experience {...props} />;
    case "education":
      return <Experience {...props} />;
    case "projects":
      return <Experience {...props} />;
    case "skills":
      return <Experience {...props} />;
    case "achivement":
      return <Experience {...props} />;
    default:
      return <About {...props} />;
  }
};
function App() {
  const [inputType, setInputType] = useState("about");
  const [resume, setResume] = useState({
    about: {},
    experince: {},
  });

  return (
    <div className="flex flex-col md:flex-row w-full gap-2  justify-start items-start">
      <div className="flex gap-2">
        <SideNavbar inputType={inputType} setInputType={setInputType} />
        <FormComponent
          component={inputType}
          resume={resume}
          setResume={setResume}
        />
      </div>

      <UserResume resume={resume} />
    </div>
  );
}

export default App;
