import React from "react";
import styled from "styled-components";
import { useLinkedIn } from "react-linkedin-login-oauth2";
import linkedin from "react-linkedin-login-oauth2/assets/linkedin.png";

function LinkedInPage() {
  const [code, setCode] = React.useState("");
  const [errorMessage, setErrorMessage] = React.useState("");
  const { linkedInLogin } = useLinkedIn({
    clientId: "86vhj2q7ukf83q",
    redirectUri: `${window.location.origin}/linkedin`,
    onSuccess: (code) => {
      console.log(code);
      setCode(code);
      setErrorMessage("");
    },
    scope: "r_emailaddress r_liteprofile",
    onError: (error) => {
      console.log(error);
      setCode("");
      setErrorMessage(error.errorMessage);
    },
  });
  const requestOptions = {
    method: "POST",
    headers: { "Content-Type": "x-www-form-urlencoded" },
    body: JSON.stringify({
      grant_type: code,
      clientId: "86vhj2q7ukf83q",
    }),
  };
  fetch("https://www.linkedin.com/oauth/v2/accessToken", requestOptions)
    .then((response) => response.json())
    .then((data) => this.setState({ postId: data.id }));

  return (
    <>
      <Wrapper>
        <img
          onClick={linkedInLogin}
          src={linkedin}
          alt="Log in with Linked In"
          style={{ maxWidth: "180px", cursor: "pointer" }}
        />

        {!code && <div>No code</div>}
        {code && (
          <div>
            <div>Authorization Code: {code}</div>
            <div>
              Follow{" "}
              <Link
                target="_blank"
                href="https://docs.microsoft.com/en-us/linkedin/shared/authentication/authorization-code-flow?context=linkedin%2Fconsumer%2Fcontext&tabs=HTTPS#step-3-exchange-authorization-code-for-an-access-token"
                rel="noreferrer"
              >
                this
              </Link>{" "}
              to continue
            </div>
          </div>
        )}
        {errorMessage && <div>{errorMessage}</div>}
      </Wrapper>
    </>
  );
}

const Wrapper = styled.div`
  padding: 16px;
  display: flex;
  flex-direction: column;
  gap: 8px;
`;

const Link = styled.a`
  font-size: 20px;
  font-weight: bold;
`;

export default LinkedInPage;
