import { useState } from "react";
import About from "./components/Form/About";
import Experience from "./components/Form/Experience";
import UserInput from "./components/UserInput";
import UserResume from "./components/UserResume";

const FormComponent = ({ component, ...props }) => {
  // console.log({ component });
  switch (component) {
    case "about":
      return <About {...props} />;
    case "experience":
      return <Experience {...props} />;
    case "education":
      return <Experience {...props} />;
    case "experience":
      return <Experience {...props} />;
    default:
      return <About {...props} />;
  }
};
function App() {
  // const [state, setState] = useState({});
  const [inputType, setInputType] = useState("about");
  return (
    <div className="flex w-full md:flex-col justify-center items-center">
      <UserInput
        className="basis-[40%]"
        inputType={inputType}
        setInputType={setInputType}
      />
      <FormComponent component={inputType} inputType={inputType} />
      <UserResume className="basic-[60%]" />
    </div>
  );
}

export default App;
