import React from "react";
import styled from "styled-components";
import { useLinkedIn } from "react-linkedin-login-oauth2";
import linkedin from "react-linkedin-login-oauth2/assets/linkedin.png";
import { Link } from "react-router-dom";
import axios from "axios";

function LinkedInPage() {
  // myclient_ed = '86inopqec1xfly'
  const [autherization_code, setCode] = React.useState("");
  const [errorMessage, setErrorMessage] = React.useState("");

  const getAccessToken = async () => {
    const response = await axios.get(
      `http://localhost:8000/getAccessToken?token=${autherization_code}`
    );

    console.log(response);
  };

  const getAuthCode = async () => {
    const response = await axios.get(
      `https://www.linkedin.com/oauth/v2/authorization?response_type=code&client_id=86inopqec1xfly&redirect_uri=http://localhost:3000&state=DCEeFWfasdf45A53sdfKef424&scope=r_liteprofile%20r_emailaddress%20w_member_social`
    );

    console.log(response);
  };

  const { linkedInLogin } = useLinkedIn({
    clientId: "86inopqec1xfly",
    redirectUri: `${
      typeof window === "object" && window.location.origin
    }/linkedin`,
    onSuccess: (code) => {
      console.log(code);
      setCode(code);
    },
    scope: "r_emailaddress",
    onError: (error) => {
      console.log(error);
      setErrorMessage(error.errorMessage);
    },
  });

  //this to get the access token
  if (autherization_code) {
    getAccessToken();
  }

  //we need to get the personal information using the autherizaton token

  return (
    <div className="flex flex-col  justify-center items-center w-full h-[100vh] ">
      <div className="p-4 bg-[red] cursor-pointer w-[120px]">
        <Link to="/normal-flow">Normal â†’</Link>
      </div>

      <Wrapper>
        <img
          onClick={linkedInLogin}
          src={linkedin}
          alt="Log in with Linked In"
          style={{ maxWidth: "180px", cursor: "pointer" }}
        />

        {!autherization_code && <div>No code</div>}
        {autherization_code && (
          <div>
            <div>Authorization Code: {autherization_code}</div>
            <div>
              Follow{" "}
              <Link
                target="_blank"
                href="https://docs.microsoft.com/en-us/linkedin/shared/authentication/authorization-code-flow?context=linkedin%2Fconsumer%2Fcontext&tabs=HTTPS#step-3-exchange-authorization-code-for-an-access-token"
                rel="noreferrer"
              >
                this
              </Link>
              to continue
            </div>
          </div>
        )}
        <button onClick={getAuthCode}>Click me</button>
        {errorMessage && <div>{errorMessage}</div>}
      </Wrapper>
    </div>
  );
}

const Wrapper = styled.div`
  padding: 16px;
  display: flex;
  flex-direction: column;
  gap: 8px;
`;

export default LinkedInPage;

