import React, { useEffect } from "react";

const UserResume = ({ resume }) => {
  const {
    first_name,
    last_name,
    phone_number,
    address,
    designation,
    email,
    city,
    description,
  } = resume.about;
  const {
    school,
    degree,
    school_city,
    start_date,
    end_date,
    education_description,
  } = resume.education;

  const [templateSelected, setTemplateSelected] = useState("Template1");

  const selectTemplate = (value) => {
    console.log("the value", value);
  };
  return (
    <div className="flex flex-col">
      <div>
        <div>Select Templates</div>
        <div className="relative w-full lg:max-w-sm">
          <select
            onChange={(e) => selectTemplate(e.target.value)}
            className="w-full p-2.5 text-black  border rounded-md shadow-sm outline-none appearance-none focus:border-indigo-600"
          >
            <option>Template1</option>
            <option>Template2</option>
          </select>
        </div>
      </div>
      <div className="flex justify-start w-[60%] mt-4  items-start">
        <div className="flex bg-[#353535] h-screen  text-white flex-col p-10 basis-[35%] ">
          <div className="underline">CONTACT</div>
          <div>{phone_number}</div>
          <div>{email}</div>
          <span>
            {address}
            {city}
          </span>
          <div className="underline">EDUCATION</div>
          <div>{school}</div>
          <div>{school_city}</div>
          <div>{degree}</div>
          <span>
            {start_date}
            {end_date}
          </span>
          <div> {education_description}</div>
        </div>
        <div className="flex flex-col basis-[65%] p-10 ">
          <div className="text-2xl flex">
            <div>{first_name}</div>
            <div>{last_name}</div>
          </div>
          <div>
            <div> {designation}</div>

            <div>{description}</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserResume;
