export const resumeUpdater = (
  resume,
  setResume,
  page,
  key,
  value,
  internal_key = null
) => {
  const UpdateData = (page, internal_key, key) => {
    return (resume[page].find((el) => el.id === internal_key).key = value);
  };

  if (page === "about" && !internal_key) {
    setResume({
      ...resume,
      [page]: {
        ...resume[page],
        [key]: value,
      },
    });
  } else {
    console.log("hello");
    setResume({
      ...resume,
      [page]: UpdateData(resume.page, internal_key, key, value),
    });
  }
};
