export const resumeUpdater = (resume, setResume, page, key, value) => {
  if (page === "about") {
    setResume({
      ...resume,
      [page]: {
        ...resume[page],
        [key]: value,
      },
    });
  } else {
    setResume({
      ...resume,
      [page]: [
        {
          ...resume[page],
          [key]: value,
        },
      ],
    });
  }
};
