export const resumeUpdater = (
  resume,
  setResume,
  page,
  key,
  value,
  internal_key = null
) => {
  if (page === "about" && !internal_key) {
    setResume({
      ...resume,
      [page]: {
        ...resume[page],
        [key]: value,
      },
    });
  } else {
    setResume({
      ...resume,
      [page]: [...resume.page, (resume.page[internal_key] = value)],
    });
  }
};
