import React from "react";
import styled from "styled-components";
import { useLinkedIn } from "react-linkedin-login-oauth2";
import linkedin from "react-linkedin-login-oauth2/assets/linkedin.png";
import { Link } from "react-router-dom";
import axios from "axios";

function LinkedInPage() {
  // myclient_ed = '86inopqec1xfly'
  const [autherization_code, setCode] = React.useState("");
  const [errorMessage, setErrorMessage] = React.useState("");

  const getAccessToken = async () => {
    let headersList = {
      "Content-Type": "application/x-www-form-urlencoded",
    };

    let bodyContent =
      "grant_type=authorization_code&code=AQR6efuDmt8Sgr8tblJJqjtJfqHiNZ5xGcSuZaBARvW2rTXnppgvyr452cFoVOsIqhgqO4KTObT4Ao4fCdE0PtaHd9tMy3jouffescpO8CdZSt3caTlUwBj5DjSyBmoYvbLEsJBw2yBtZS1E9AYvqO7YBpMl2zNoHc06e8Qq781G01WALF7Ul6Hz2Heq0d1l_kr5I2bKxndP2PEM09o&client_id=86inopqec1xfly&client_secret=SnweqQtf72g37t5t&redirect_uri=http://localhost:3000/linkedin";

    let reqOptions = {
      url: "https://www.linkedin.com/oauth/v2/accessToken",
      method: "POST",
      headers: headersList,
      data: bodyContent,
    };

    let response = await axios.request(reqOptions);
    console.log(response.data);
  };

  const { linkedInLogin } = useLinkedIn({
    clientId: "86vhj2q7ukf83q",
    redirectUri: `${window.location.origin}/linkedin`,
    onSuccess: (code) => {
      console.log(code);
      setCode(code);
      setErrorMessage("");
    },
    scope: "r_emailaddress r_liteprofile",
    onError: (error) => {
      console.log(error);
      setCode("");
      setErrorMessage(error.errorMessage);
    },
  });

  //this to get the access token
  if (autherization_code) {
    getAccessToken();
  }

  //we need to get the personal information using the autherizaton token

  return (
    <div className="flex flex-col  justify-center items-center w-full h-[100vh] ">
      <div className="p-4 bg-[red] cursor-pointer w-[120px]">
        <Link to="/normal-flow">Normal â†’</Link>
      </div>

      <Wrapper>
        <img
          onClick={linkedInLogin}
          src={linkedin}
          alt="Log in with Linked In"
          style={{ maxWidth: "180px", cursor: "pointer" }}
        />

        {!autherization_code && <div>No code</div>}
        {autherization_code && (
          <div>
            <div>Authorization Code: {autherization_code}</div>
            <div>
              Follow{" "}
              <Link
                target="_blank"
                href="https://docs.microsoft.com/en-us/linkedin/shared/authentication/authorization-code-flow?context=linkedin%2Fconsumer%2Fcontext&tabs=HTTPS#step-3-exchange-authorization-code-for-an-access-token"
                rel="noreferrer"
              >
                this
              </Link>
              to continue
            </div>
          </div>
        )}
        {errorMessage && <div>{errorMessage}</div>}
      </Wrapper>
    </div>
  );
}

const Wrapper = styled.div`
  padding: 16px;
  display: flex;
  flex-direction: column;
  gap: 8px;
`;

export default LinkedInPage;
